2016\n\nСкрипт получает все значения доп. полей ресурса, содержащего настройки, и создаёт одноимённые плейсхолдеры с префиксом \"set.\", пример:\n[[+set.site-slogan]]\nКонтакты хранятся в migx-е, поэтому для них предусмотрена обработка и вывод сразу с иконками и ссылками.\nВыводятся 2 списка контактов: [[+set.contacts.header]] и [[+set.contacts.all]] (если у контакта стоит галка dont_show, то он не появится в этих списках)\nА также каждый контакт выводится в таком виде: [[+set.contacts.email1]] и в таком: [[+set.unique_name]] (если у контакта установлено unique_name)\nАдрес почты в списках \"всех контактов\" и \"для хедера\" выводятся через сниппет botaway\n\nПараметры:\n    contactTpl - шаблон для каждого контакта (нужно указать имя чанка, либо html код с префиксом \"@INLINE \"),\n        значение по умолчанию: \'<p class=\"[[+class]]\"><span>[[+icon]] <strong>[[+text]]</strong> [[+value]]</span></p>\'\n    settings_res_id - id ресурса, содержащего настройки\n*/\n\nclass GetSiteSettings\n{\n    private $default_contact_tpl = \'<p class=\"[[+class]]\"><span>[[+icon]] <strong>[[+text]]</strong> [[+value]]</span></p>\';\n\n    public function __construct($modx, $scriptProperties)\n    {\n        $this->modx = $modx;\n        $this->settings_res_id = $scriptProperties[\'settings_res_id\'];\n\n        // устанавливаем шаблон контакта (из чанка, из инлайна или дефолтный)\n        if (isset($scriptProperties[\'contactTpl\'])) {\n            if (strpos($scriptProperties[\'contactTpl\'], \'@INLINE\') === 0) {\n                $this->contact_tpl = substr($scriptProperties[\'contactTpl\'], 8);\n            } else {\n                $this->contact_tpl = $this->modx->getChunk($scriptProperties[\'contactTpl\']);\n            }\n        } else {\n            $this->contact_tpl = $this->default_contact_tpl;\n        }\n\n        // получаем все \"настройки сайта\"\n        $stmt = $this->modx->query(\"\n            SELECT tv.name, tv_c.value\n            FROM modx_site_tmplvars as tv\n\n            LEFT JOIN modx_site_tmplvar_contentvalues as tv_c\n            ON tv.id = tv_c.tmplvarid and tv_c.contentid = \" . $this->settings_res_id . \"\n        \");\n\n        $placeholders = array();\n        while ($row = $stmt->fetch()) {\n            if ($row[\'name\'] == \'contacts\') { // для тв-поля contacts - особая обработка:\n                $contacts = $this->processContacts($row[\'value\']);\n                $placeholders = array_merge($placeholders, $contacts);\n            } else { // важно: если значение пустое, то присваиваем ему пустую строку\n                $pl_name = \'set.\' . $row[\'name\'];\n                $placeholders[$pl_name] = $row[\'value\'] ? $row[\'value\'] : \'\';\n            }\n        }\n        $modx->setPlaceholders($placeholders); // установка плейсхолдеров - весь результат работы скрипта\n    }\n\n    private function fixLink($link)\n    {\n        if (strpos($link, \'http\') !== 0) {\n            $link = \'http://\' . $link;\n        }\n        return $link;\n    }\n\n    private function clearPhone($input)\n    {\n        return preg_replace(array(\'/[^\\d]/u\', \'/^[7|8]/u\'), array(\'\', \'+7\'), $input);\n    }\n\n    private function processContacts($json_str)\n    {\n        $contacts_arr = json_decode($json_str);\n        $result = array();\n        $item_counter = array(); // счётчик для каждого из типов контактов\n        foreach($contacts_arr as $contact) {\n            $item_html = \'\';\n            // ужасная проверка по строкам на русском языке, но иначе нельзя:\n            switch($contact->contact_type) {\n                case \'Телефон\':\n                    $item_icon = \'<i class=\"fa fa-fw fa-phone\"></i>\';\n                    $cleared_phone = $this->clearPhone($contact->contact_value);\n                    $item_value_wrapped = \'<a href=\"tel:\' . $cleared_phone . \'\">\' . $contact->contact_value . \'</a>\';\n                    $item_type = \'phone\';\n                    break;\n                case \'Факс\':\n                    $item_icon = \'<i class=\"fa fa-fw fa-fax\"></i>\';\n                    $cleared_phone = $this->clearPhone($contact->contact_value);\n                    $item_value_wrapped = \'<a href=\"tel:\' . $cleared_phone . \'\">\' . $contact->contact_value . \'</a>\';\n                    $item_type = \'fax\';\n                    break;\n                case \'E-mail\':\n                    $item_icon = \'<i class=\"fa fa-fw fa-envelope-o\"></i>\';\n                    $item_value_wrapped = $this->modx->runSnippet(\'botaway\', array(\'email\' => $contact->contact_value));\n                    //$item_value_wrapped = \'<a href=\"mailto:\' . $contact->contact_value . \'\">\' . $contact->contact_value . \'</a>\';\n                    $item_type = \'email\';\n                    break;\n                case \'Адрес\':\n                    $item_icon = \'<i class=\"fa fa-fw fa-map-marker\"></i>\';\n                    $item_value_wrapped = $contact->contact_value;\n                    $item_type = \'address\';\n                    break;\n                case \'Skype\':\n                    $item_icon = \'<i class=\"fa fa-fw fa-skype\"></i>\';\n                    $item_value_wrapped = $contact->contact_value;\n                    $item_type = \'skype\';\n                    break;\n                case \'ВКонтакте\':\n                    $item_icon = \'<i class=\"fa fa-fw fa-vk\"></i>\';\n                    $link = $this->fixLink($contact->contact_value);\n                    $item_value_wrapped = \'<a target=\"_blank\" href=\"\' . $link . \'\">ВКонтакте</a>\';\n                    $item_type = \'vk\';\n                    break;\n                case \'Facebook\':\n                    $item_icon = \'<i class=\"fa fa-fw fa-facebook-official\"></i>\';\n                    $link = $this->fixLink($contact->contact_value);\n                    $item_value_wrapped = \'<a target=\"_blank\" href=\"\' . $link . \'\">Facebook</a>\';\n                    $item_type = \'facebook\';\n                    break;\n                case \'Instagram\':\n                    $item_icon = \'<i class=\"fa fa-fw fa-instagram\"></i>\';\n                    $link = $this->fixLink($contact->contact_value);\n                    $item_value_wrapped = \'<a target=\"_blank\" href=\"\' . $link . \'\">Instagram</a>\';\n                    $item_type = \'instagram\';\n                    break;\n                case \'Веб-сайт\':\n                    $item_icon = \'<i class=\"fa fa-fw fa-external-link\"></i>\';\n                    $link = $this->fixLink($contact->contact_value);\n                    $item_value_wrapped = \'<a target=\"_blank\" href=\"\' . $link . \'\">\' . $link . \'</a>\';\n                    $item_type = \'website\';\n                    break;\n                case \'Время\':\n                    $item_icon = \'<i class=\"fa fa-fw fa-clock-o\" aria-hidden=\"true\"></i>\';\n                    $link = $this->fixLink($contact->contact_value);\n                    $item_value_wrapped = \'<a target=\"_blank\" href=\"\' . $link . \'\">\' . $link . \'</a>\';\n                    $item_type = \'time\';\n                    break;\n                case \'Текст\':\n                    $item_icon = \'\';\n                    $item_value_wrapped = $contact->contact_value;\n                    $item_type = \'text\';\n                    break;\n            }\n\n            $item_html = str_replace(\n                array(\'[[+icon]]\', \'[[+value]]\', \'[[+text]]\', \'[[+class]]\'),\n                array($item_icon, $item_value_wrapped, $contact->contact_text, $contact->css_class),\n                $this->contact_tpl\n            );\n\n            // значения contacts.all и contacts.header\n            if ($contact->contact_value && !$contact->dont_show) {\n                $result[\'set.contacts.all\'] .= $item_html;\n                if ($contact->display_at_header) {\n                    $result[\'set.contacts.header\'] .= $item_html;\n                }\n            }\n\n            // значения плейсхолдеров со счётчиками\n            $item_counter[$item_type]++;\n            $item_placeholder = \'set.contacts.\' . $item_type . $item_counter[$item_type];\n            $result[$item_placeholder] = $contact->contact_value;\n            // доптекст поля\n            $item_text_placeholder = \'set.contacts.\' . $item_type . $item_counter[$item_type] . \'.text\';\n            $result[$item_text_placeholder] = $contact->contact_text;\n\n            // выкидываем лишние символы из уникального имени\n            $contact->unique_name = preg_replace(\'/[^\\w-]/\', \'\', $contact->unique_name);\n            // добавляем уникальное имя контакта (если оно заполнено)\n            if ($contact->unique_name) {\n                $result[\'set.\' . $contact->unique_name] = $contact->contact_value;\n                $result[\'set.\' . $contact->unique_name . \'.text\'] = $contact->contact_text;\n            }\n\n            //\n        }\n        return $result;\n    }\n}\n\nnew GetSiteSettings($modx, $scriptProperties);',0,'a:1:{s:15:\"settings_res_id\";a:7:{s:4:\"name\";s:15:\"settings_res_id\";s:4:\"desc\";s:0:\"\";s:4:\"type\";s:9:\"textfield\";s:7:\"options\";a:0:{}s:5:\"value\";s:2:\"17\";s:7:\"lexicon\";N;s:4:\"area\";s:0:\"\";}}','',0,''),(42,0,0,'pthumb','Identical to phpthumbof. Better for future compatibility.\nDocumentation: https://github.com/oo12/phpThumbOf',0,24,0,'/**\n * pThumb\n * Copyright 2013-2014 Jason Grant\n *\n * Please see the GitHub page for documentation or to report bugs:\n * https://github.com/oo12/phpThumbOf\n *\n * Forked from phpThumbOf 1.4.0\n * Copyright 2009-2012 by Shaun McCormick <shaun@modx.com>\n *\n * pThumb is free software; you can redistribute it and/or modify it\n * under the terms of the GNU General Public License as published by the Free\n * Software Foundation; either version 2 of the License, or (at your option) any\n * later version.\n *\n * pThumb is distributed in the hope that it will be useful, but WITHOUT ANY\n * WARRANTY; without even the implied warranty of MERCHANTABILITY or FITNESS FOR\n * A PARTICULAR PURPOSE. See the GNU General Public License for more details.\n *\n * You should have received a copy of the GNU General Public License along with\n * phpThumbOf; if not, write to the Free Software Foundation, Inc., 59 Temple\n * Place, Suite 330, Boston, MA 02111-1307 USA\n *\n */\n/**\n *\n * @var modX $modx\n * @var array $scriptProperties\n * @var string $input\n * @var string|array $options\n *\n */\n\nif (empty($input)) {  // Exit quietly if no file name given\n	return;\n}\n\n$scriptProperties[\'debug\'] = isset($debug) ? $debug : false;\n\n\nstatic $pt_settings = array();\n\nif (empty($pt_settings)) {\n	if (!$modx->loadClass(\'phpThumbOf\', MODX_CORE_PATH . \'components/phpthumbof/model/\', true, true)) {\n		$modx->log(modX::LOG_LEVEL_ERROR, \'[pThumb] Could not load phpThumbOf class.\');\n		return $input;\n	}\n}\n\n$pThumb = new phpThumbOf($modx, $pt_settings, $scriptProperties);\n\n$result = $pThumb->createThumbnail($input, $options);\n\nif (!empty($toPlaceholder) || $result[\'outputDims\']) {\n	if ($result[\'width\'] === \'\' && $result[\'file\'] && $dims = getimagesize($result[\'file\']) ) {\n			$result[\'width\'] = $dims[0];\n			$result[\'height\'] = $dims[1];\n	}\n	if (!empty($toPlaceholder)) {\n		$modx->setPlaceholders(array(\n			$toPlaceholder => $result[\'src\'],\n			\"$toPlaceholder.width\" => $result[\'width\'],\n			\"$toPlaceholder.height\" => $result[\'height\']\n		));\n		$output = \'\';\n	}\n	if ($result[\'outputDims\']) {\n		$output = \"src=\\\"{$result[\'src\']}\\\"\" . ($result[\'width\'] ? \" width=\\\"{$result[\'width\']}\\\" height=\\\"{$result[\'height\']}\\\"\" : \'\');\n	}\n}\nelse {\n	$output = $result[\'src\'];\n}\n\nif ($debug && $result[\'success\']) {  // if debugging is on and createThumbnail was successful, log the debug info\n	$pThumb->debugmsg(isset($pThumb->phpThumb->debugmessages) ? \':: Processed ::\' : \":: Loaded from cache: {$result[\'src\']}\", true);\n}\n\nreturn $output;',0,'a:1:{s:5:\"debug\";a:7:{s:4:\"name\";s:5:\"debug\";s:4:\"desc\";s:22:\"prop_pthumb.debug_desc\";s:4:\"type\";s:13:\"combo-boolean\";s:7:\"options\";a:0:{}s:5:\"value\";b:0;s:7:\"lexicon\";s:18:\"phpthumbof:default\";s:4:\"area\";s:0:\"\";}}','',0,''),(43,2,0,'viewCounter','',0,5,0,'$current_res = $modx->resource;\n$current_res_id = $current_res->get(\'id\');\n\nif (!isset($_SESSION[\'viewed-pages\'])) {\n    $_SESSION[\'viewed-pages\'] = array();\n}\n\nif (!isset($_SESSION[\'viewed-pages\'][$current_res_id])) {\n    $_SESSION[\'viewed-pages\'][$current_res_id] = true;\n    $views = $current_res->getTVValue(\'views\');\n    $current_res->setTVValue(\'views\', ++$views);\n}',0,'a:0:{}','',0,''),(44,0,0,'getImageList','',0,26,0,'/**\n * getImageList\n *\n * Copyright 2009-2014 by Bruno Perner <b.perner@gmx.de>\n *\n * getImageList is free software; you can redistribute it and/or modify it\n * under the terms of the GNU General Public License as published by the Free\n * Software Foundation; either version 2 of the License, or (at your option) any\n * later version.\n *\n * getImageList is distributed in the hope that it will be useful, but WITHOUT ANY\n * WARRANTY; without even the implied warranty of MERCHANTABILITY or FITNESS FOR\n * A PARTICULAR PURPOSE. See the GNU General Public License for more details.\n *\n * You should have received a copy of the GNU General Public License along with\n * getImageList; if not, write to the Free Software Foundation, Inc., 59 Temple Place,\n * Suite 330, Boston, MA 02111-1307 USA\n *\n * @package migx\n */\n/**\n * getImageList\n *\n * display Items from outputvalue of TV with custom-TV-input-type MIGX or from other JSON-string for MODx Revolution \n *\n * @version 1.4\n * @author Bruno Perner <b.perner@gmx.de>\n * @copyright Copyright &copy; 2009-2014\n * @license http://www.gnu.org/licenses/old-licenses/gpl-2.0.html GNU General Public License\n * version 2 or (at your option) any later version.\n * @package migx\n */\n\n/*example: <ul>[[!getImageList? &tvname=`myTV`&tpl=`@CODE:<li>[[+idx]]<img src=\"[[+imageURL]]\"/><p>[[+imageAlt]]</p></li>`]]</ul>*/\n/* get default properties */\n\n\n$tvname = $modx->getOption(\'tvname\', $scriptProperties, \'\');\n$tpl = $modx->getOption(\'tpl\', $scriptProperties, \'\');\n$wrapperTpl = $modx->getOption(\'wrapperTpl\', $scriptProperties, \'\');\n$limit = $modx->getOption(\'limit\', $scriptProperties, \'0\');\n$offset = $modx->getOption(\'offset\', $scriptProperties, 0);\n$totalVar = $modx->getOption(\'totalVar\', $scriptProperties, \'total\');\n$randomize = $modx->getOption(\'randomize\', $scriptProperties, false);\n$preselectLimit = $modx->getOption(\'preselectLimit\', $scriptProperties, 0); // when random preselect important images\n$where = $modx->getOption(\'where\', $scriptProperties, \'\');\n$where = !empty($where) ? $modx->fromJSON($where) : array();\n$sort = $modx->getOption(\'sort\', $scriptProperties, \'\');\n$sort = !empty($sort) ? $modx->fromJSON($sort) : array();\n$toSeparatePlaceholders = $modx->getOption(\'toSeparatePlaceholders\', $scriptProperties, false);\n$toPlaceholder = $modx->getOption(\'toPlaceholder\', $scriptProperties, false);\n$outputSeparator = $modx->getOption(\'outputSeparator\', $scriptProperties, \'\');\n$splitSeparator = $modx->getOption(\'splitSeparator\', $scriptProperties, \'\');\n$placeholdersKeyField = $modx->getOption(\'placeholdersKeyField\', $scriptProperties, \'MIGX_id\');\n$toJsonPlaceholder = $modx->getOption(\'toJsonPlaceholder\', $scriptProperties, false);\n$jsonVarKey = $modx->getOption(\'jsonVarKey\', $scriptProperties, \'migx_outputvalue\');\n$outputvalue = $modx->getOption(\'value\', $scriptProperties, \'\');\n$outputvalue = isset($_REQUEST[$jsonVarKey]) ? $_REQUEST[$jsonVarKey] : $outputvalue;\n$docidVarKey = $modx->getOption(\'docidVarKey\', $scriptProperties, \'migx_docid\');\n$docid = $modx->getOption(\'docid\', $scriptProperties, (isset($modx->resource) ? $modx->resource->get(\'id\') : 1));\n$docid = isset($_REQUEST[$docidVarKey]) ? $_REQUEST[$docidVarKey] : $docid;\n$processTVs = $modx->getOption(\'processTVs\', $scriptProperties, \'1\');\n$reverse = $modx->getOption(\'reverse\', $scriptProperties, \'0\');\n$sumFields = $modx->getOption(\'sumFields\', $scriptProperties, \'\');\n$sumPrefix = $modx->getOption(\'sumPrefix\', $scriptProperties, \'summary_\');\n$addfields = $modx->getOption(\'addfields\', $scriptProperties, \'\');\n$addfields = !empty($addfields) ? explode(\',\', $addfields) : null;\n//split json into parts\n$splits = $modx->fromJson($modx->getOption(\'splits\', $scriptProperties, 0));\n$splitTpl = $modx->getOption(\'splitTpl\', $scriptProperties, \'\');\n$splitSeparator = $modx->getOption(\'splitSeparator\', $scriptProperties, \'\');\n$inheritFrom = $modx->getOption(\'inheritFrom\', $scriptProperties, \'\'); //commaseparated list of resource-ids or/and the keyword \'parents\' where to inherit from\n$inheritFrom = !empty($inheritFrom) ? explode(\',\',$inheritFrom) : \'\';\n\n$modx->setPlaceholder(\'docid\', $docid);\n\n$base_path = $modx->getOption(\'base_path\', null, MODX_BASE_PATH);\n$base_url = $modx->getOption(\'base_url\', null, MODX_BASE_URL);\n\n$migx = $modx->getService(\'migx\', \'Migx\', $modx->getOption(\'migx.core_path\', null, $modx->getOption(\'core_path\') . \'components/migx/\') . \'model/migx/\', $scriptProperties);\nif (!($migx instanceof Migx))\n    return \'\';\n$migx->working_context = isset($modx->resource) ? $modx->resource->get(\'context_key\') : \'web\';\n\nif (!empty($tvname)) {\n    if ($tv = $modx->getObject(\'modTemplateVar\', array(\'name\' => $tvname))) {\n\n        /*\n        *   get inputProperties\n        */\n\n\n        $properties = $tv->get(\'input_properties\');\n        $properties = isset($properties[\'formtabs\']) ? $properties : $tv->getProperties();\n\n        $migx->config[\'configs\'] = $modx->getOption(\'configs\', $properties, \'\');\n        if (!empty($migx->config[\'configs\'])) {\n            $migx->loadConfigs();\n            // get tabs from file or migx-config-table\n            $formtabs = $migx->getTabs();\n        }\n        if (empty($formtabs) && isset($properties[\'formtabs\'])) {\n            //try to get formtabs and its fields from properties\n            $formtabs = $modx->fromJSON($properties[\'formtabs\']);\n        }\n\n        if (!empty($properties[\'basePath\'])) {\n            if ($properties[\'autoResourceFolders\'] == \'true\') {\n                $scriptProperties[\'base_path\'] = $base_path . $properties[\'basePath\'] . $docid . \'/\';\n                $scriptProperties[\'base_url\'] = $base_url . $properties[\'basePath\'] . $docid . \'/\';\n            } else {\n                $scriptProperties[\'base_path\'] = $base_path . $properties[\'base_path\'];\n                $scriptProperties[\'base_url\'] = $base_url . $properties[\'basePath\'];\n            }\n        }\n        if ($jsonVarKey == \'migx_outputvalue\' && !empty($properties[\'jsonvarkey\'])) {\n            $jsonVarKey = $properties[\'jsonvarkey\'];\n            $outputvalue = isset($_REQUEST[$jsonVarKey]) ? $_REQUEST[$jsonVarKey] : $outputvalue;\n        }\n        \n        if (empty($outputvalue)){\n            $outputvalue = $tv->renderOutput($docid);\n            if (empty($outputvalue) && !empty($inheritFrom)){\n                foreach ($inheritFrom as $from){\n                    if ($from == \'parents\'){\n                        $outputvalue = $tv->processInheritBinding(\'\',$docid);\n                    }else{\n                        $outputvalue = $tv->renderOutput($from);\n                    }\n                    if (!empty($outputvalue)){\n                        break;\n                    }                    \n                }\n            }\n        }\n\n       \n        /*\n        *   get inputTvs \n        */\n        $inpu